import { update } from './../BooksAPI'

const ShelfChanger = ({ book, updateLibrary, bookShelf, setBookShelf }) => {
  console.log("- bookShelf", bookShelf);
  const handleChange = (e) => setBookShelf(e.target.value)

  const updateBook = async (book, bookShelf) => {
    await update(book, bookShelf)
  }

  return (

    <div className="book-shelf-changer">

      <select defaultValue={`${book.shelf}`} onChange={(e) => { handleChange(e), updateBook() }}>

        <option value="none" disabled>
          Move to...
        </option>
        <option value="currentlyReading">
          Currently Reading
        </option>
        <option value="wantToRead">Want to Read</option>
        <option value="read">Read</option>
        <option value="none">None</option>

      </select>
    </div>

  )
}

export default ShelfChanger
