import "./App.css";
import { useState, useEffect } from "react";
// import { BrowserRouter as Router, Switch, Route } from 'react-router-dom'

import books from './data/books'
import Header from './components/Header'
import BookCard from './components/BookCard'
import BookShelf from "./components/BookShelf";
import { get, getAll } from './BooksAPI'

function App() {
  const [showSearchPage, setShowSearchpage] = useState(false);

  useEffect(() => {
    const bookId = 'yDtCuFHXbAYC&print';

    const fetchData = async () => {
      const result = await get(bookId)
      const getAllBooks = await getAll()
      console.log("- getAllBooks", getAllBooks);
      console.log("- result", result);
    }

    fetchData()
  }, [])

  return (
    <div className="app">
      {showSearchPage ? (
        <div className="search-books">
          <div className="search-books-bar">
            <a
              className="close-search"
              onClick={() => setShowSearchpage(!showSearchPage)}
            >
              Close
            </a>
            <div className="search-books-input-wrapper">
              <input
                type="text"
                placeholder="Search by title, author, or ISBN"
              />
            </div>
          </div>
          <div className="search-books-results">
            <ol className="books-grid"></ol>
          </div>
        </div>
      ) : (
        <div className="list-books">
          <Header />
          <div className="list-books-content">

            <div>
              <BookShelf title="Currently Reading">
                {
                  books.map(book => {
                    if (book.state === 'Currently Reading') {
                      return (
                        <BookCard book={book} key={book.id} />
                      )
                    }
                  })
                }
              </BookShelf>

              <BookShelf title="Want to Read">
                {
                  books.map(book => {
                    if (book.state === 'Want to Read') {
                      return (
                        <BookCard book={book} key={book.id} />
                      )
                    }
                  })
                }
              </BookShelf>

              <BookShelf title="Read">
                {
                  books.map(book => {
                    if (book.state === 'Read') {
                      return (<BookCard book={book} key={book.id} />)
                    }
                  })
                }
              </BookShelf>

            </div>
          </div>
          <div className="open-search">
            <a onClick={() => setShowSearchpage(!showSearchPage)}>Add a book</a>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
